var body=document.querySelector("body"),menuTrigger=document.querySelector("#toggle-main-menu-mobile"),menuContainer=document.querySelector("#main-menu-mobile");menuTrigger.onclick=function(){menuContainer.classList.toggle("open"),menuTrigger.classList.toggle("is-active"),body.classList.toggle("lock-scroll")},window.addEventListener("scroll",e=>{const t=document.querySelector(".header");window.scrollY>=44?t.classList.add("scroll"):t.classList.remove("scroll")})